mapper.select:
SELECT

FROM readenjoy_user
WHERE username = #{username}
AND password = #{password}

SELECT question
FROM readenjoy_user
WHERE username = #{username}

SELECT count(1)
FROM readenjoy_user
WHERE username = #{username}
AND question = #{question}
AND answer = #{answer}

mapper.update:
UPDATE readenjoy_user
SET password = #{passwordNew},update_time = now()
WHERE username = #{username}

mapper.select:
SELECT COUNT(1)
FROM readenjoy_user
WHERE id = #{userId}
AND password = #{passwordOld}

SELECT COUNT(1) FROM readenjoy_user
WHERE email = #{email}
AND id != #{userId}
